targets.expr:
topk(25,
  (
  sum by (job,device_name,if_interface_name,if_Description) (
  rate(kentik_snmp_ifHCOutOctets{job=~"$job",device_name=~"$instance",if_interface_name=~"$ifName"} [$__rate_interval])
)
)*8

# set max limit to workaround for TB-PB/sec spikes when counters are overloaded too quickly on very busy interfaces.
<100*10^9
)

topk(25,

  (
  sum by (job,device_name,if_interface_name,if_Description) (
  rate(kentik_snmp_ifHCInOctets{job=~"$job",device_name=~"$instance",if_interface_name=~"$ifName"}[$__rate_interval])
)
)*8

# set max limit to workaround for TB-PB/sec spikes when counters are overloaded too quickly on very busy interfaces.
<100*10^9
)
